INSERT INTO `guidelines` (`id`, `language`, `subject`, `slug`, `content`, `file`, `image`, `meta_title`, `meta_description`, `status`, `created_at`, `updated_at`) VALUES
(1, 'fa', 'رهنمود تدوین و تطبیق تفاهم نامه ها', 'رهنمود-تدوین-و-تطبیق-تفاهم-نامه-ها', '<p>این رهنمود در راستای امور مربوط به تسوید، طی مراحل و تطبیق تفاهمنامه های همکاری میان اداره تعلیمات تخنیکی و مسلکی با سایر مراجع و نهاد های دولتی، خصوصی، غیر دولتی و بین المللی تنظیم گردیده و تمام ریاست ها و شعبات ذیربط اداره تعلیمات تخنیکی و مسلکی در مرکز و ولایات مکلف به تطبیق و رعایت مندرجات آن در بخش های کاری شان می باشند.</p>', 'uploads/KD6x1jQxEq1RBFyEAUHR7KieelW1yI.jpg', 'uploads/008oORAfIkqmKK0gCT9yGAyMU0otVG.jpg', 'رهنمود تدوین و تطبیق تفاهم نامه ها', 'این رهنمود در راستای امور مربوط به تسوید، طی مراحل و تطبیق تفاهمنامه های همکاری میان اداره تعلیمات تخنیکی و مسلکی با سایر مراجع و نهاد های دولتی، خصوصی، غیر دولتی و بین المللی تنظیم گردیده.', 1, '2023-12-11 04:37:43', '2023-12-11 04:37:43'),
(2, 'fa', 'رهنمود تدوین و توسعه پالیسی ها', 'رهنمود-تدوین-و-توسعه-پالیسی-ها', '<p>این رهنمود منحیث سند رسمی برای تدوین و تطبیق اسناد پالیسی در اداره تعلیمات تخنیکی و مسلکی ایجاد شده است. تمام بخش های این اداره که مسؤلیت تدوین، تطبیق، نظارت و ارزیابی اسناد پالیسی را به عهده دارند، قابل تطبیق است.</p>', 'uploads/g8IBEBfgp4keTQCRb18yzjpvvujUBb.jpg', 'uploads/uaHyZmX5AkSWk1PneycUsDwttIflcs.jpg', 'رهنمود طرح، تدوین و توسعه پالیسی ها', 'این رهنمود منحیث سند رسمی برای تدوین و تطبیق اسناد پالیسی در اداره تعلیمات تخنیکی و مسلکی ایجاد شده است. تمام بخش های این اداره که مسؤلیت تدوین، تطبیق، نظارت و ارزیابی اسناد پالیسی را به عهده دارند، قابل تطبیق است.', 1, '2023-12-11 05:27:21', '2023-12-11 05:27:21'),
(3, 'fa', 'رهنمود تأسیس، ایجاد رشته، ارتقاء، ادغام، انتقال، تغیر نام و لغو رشته مراکز آموزشی تعلیمات تخنیکی و مسلکی', 'رهنمود-تأسیس-ایجاد-رشته-ارتقاء-ادغام-انتقال-تغیر-نام-و-لغو-رشته-مراکز-آموزشی-تعلیمات-تخنیکی-و-مسلکی', '<p>رهنمود تأسیس، ایجاد رشته، ارتقاء، ادغام، انتقال، تغیر نام و لغو رشته مراکز آموزشی تعلیمات تخنیکی و مسلکی</p>', 'uploads/XzuZLJOjD8QT5n5Zze3IIoTJyBUOrb.jpg', 'uploads/8JVUa6C251h4t3sgoZL1SjsPd6HoWa.jpg', 'رهنمود تأسیس، ایجاد رشته، ارتقاء، ادغام، انتقال، تغیر نام و لغو رشته مراکز آموزشی تعلیمات تخنیکی و مسلکی', 'رهنمود تأسیس، ایجاد رشته، ارتقاء، ادغام، انتقال، تغیر نام و لغو رشته مراکز آموزشی تعلیمات تخنیکی و مسلکی', 1, '2023-12-11 05:29:22', '2023-12-11 05:29:22');

