INSERT INTO `inovations` (`id`, `language`, `name`, `slug`, `content`, `address`, `image`, `views`, `student_name`, `student_father_name`, `student_grand_father_name`, `date_gregorian`, `date_ghamari`, `date_shamsi`, `meta_title`, `meta_description`, `status`, `is_approved`, `province_id`, `district_id`, `center_type_id`, `created_at`, `updated_at`) VALUES
(1, 'fa', 'موتر نظامی برقی', 'موتر-نظامی-برقی', '<p>یکی از دانش آموزان خلاق لیسه میخانیکی ولسوالی زرمت ولایت پکتیا، موتر نظامی برقی ساخته که از راه دور کنترل می‌شود.</p><p>این موتر از فاصله 100 متری قابلیت کنترل دارد و بر فراز آن سلاح نیز نصب شده است. سلاح این موتر توانایی چرخش و شلیک به چهار جهت را دارد.</p><p>سازنده این موتر عبدالهادی، شاگرد صنف دوازدهم رشته انجنیری برق لیسه میخانیکی ولسوالی زرمت پکتیا است.</p><p>وی برای این موتر فرمولی نیز ساخته که افراد دیگر نیز می‌توانند با استفاده از آن فرمول موتر برقی مشابه بسازند.</p>', 'ولسوالی زرمت ولایت پکتیا', 'uploads/JFsn6GC4EoYD7A4xtIjnYyYHwEDhOz.jpg', 0, 'عبدالهادی', '.', '.', '2023-11-26', '1445-5-12', '1402-9-5', 'موتر نظامی برقی', 'یکی از دانش آموزان خلاق لیسه میخانیکی ولسوالی زرمت ولایت پکتیا، موتر نظامی برقی ساخته که از راه دور کنترل می‌شود.', 1, 0, 11, 150, 4, '2023-11-26 01:09:13', '2023-11-26 01:09:13'),
(2, 'fa', 'موتر شش تایره', 'موتر-شش-تایره', '<p>حشمت‌الله اغاجان فرزند عبدالودود باشنده ولایت بلخ از محصلین رشته تخنیک موتر، انستیتوت نفت و گاز ولایت بلخ یک موتر شش تایره ساخته است.&nbsp;</p><p>او می‌گوید که این موتر را با هزینه شخصی خود، به مبلغ بیش از پنج صد هزار افغانی در طی یک سال ساخته است.</p><p>به گفته‌ی حشمت‌الله، استادان انستیتوت نفت و گاز بلخ نیز او را در ساخت این موتر راهنمایی کرده‌اند.</p><p>وی می‌گوید در صورت فراهم‌شدن امکانات و حمایت حکومت، می‌تواند به کارش ادامه دهد و موترهای مختلف بسازد.</p><p>این موتر برای تشویق محصلین جدید رشته تخنیک موتر انستیتوت نفت و گاز بلخ به نمایش گذاشته شد.</p><p>مولوی نورالله صلاح‌الدین، آمر تعلیمات تخنیکی و مسلکی از این موتر دیدن کرد و سازنده آن را مورد تحسین قرار داد.</p><p>مولوی صلاح‌الدین هنگام بازدید از این موتر ساخت حشمت‌الله خان در مورد اهمیت تعلیمات تخنیکې و مسلکی صحبت کرده و از محصلین خواست برای ابتکار و خلاقیت دست بکار شوند.</p><p>تربیه نیروی ماهر و متخصص یکی از اهداف اداره تعلیمات تخنیکی و مسلکی است؛ در شرایطی که کشور برای توسعه و پیشرفت، نیاز جدی به نیروی متخصص دارد.</p>', 'ولایت بلخ، مزارشریف', 'uploads/7F2tcknCqDGHhTol5SXIsnNeXZ1rWH.jpg', 0, 'حشمت الله آغاجان', 'عبدالودود', '.', '2023-11-26', '1445-5-12', '1402-9-5', 'ساخت موتر شش تایره', 'محصل رشته تخنیک موتر، انستیتوت نفت و گاز ولایت بلخ یک موتر شش تایره ساخته است.', 1, 0, 9, 198, 3, '2023-11-26 01:19:47', '2023-11-26 01:19:47'),
(3, 'fa', 'دو عراده تانک نظامی', 'دو-عراده-تانک-نظامی', '<p>در یک اقدام خلاقانه یکی از محصلین خلاق انستیتوت میخانیکی ولایت خوست دو عراده تانک نظامی (هاموی و زنجیردار) ساخته است.</p><p>این وسایط توسط ریموت کنترل می‌شود.</p><p>سازنده این وسایط نظامی قسمت‌الله، محصل صنف دهم انستیتوت میخانیکی ولایت خوست است.&nbsp;</p><p>او می‌گوید که این دو عراده تانک را طی دو ماه ساخته و هفت هزار افغانی هزینه برداشته است.</p><p>قسمت‌الله می‌گوید که در صورت فراهم‌شدن امکانات و مواد خام، می‌تواند وسایط کاربردی بسازد.</p><p>تربیه نیروی خلاق، ماهر و متخصص مطابق بازار کار از اهداف اصلی اداره تعلیمات تخنیکی و مسلکی است.</p>', 'خوست، ولسوالی متون', 'uploads/1eaRPvStKpdtU8nQMYtr0uJ5nIz3Lx.jpg', 0, 'قسمت الله', '.', '.', '2023-11-26', '1445-5-12', '1402-9-5', 'دو عراده تانک نظامی', 'ساخت دو عراده تانک نظامی (هاموی و زنجیردار) ساخته است.', 1, 0, 16, 236, 3, '2023-11-26 01:50:59', '2023-11-26 01:50:59'),
(4, 'fa', 'ساخت دستگاه دزدگیر', 'ساخت-دستگاه-دزدگیر', '<p>محمد مهران &nbsp;فرزند محمد امین، یکی از شاگردان صنف دهم رشته الکترونیک انستیتوت نفت و گاز ولایت بلخ، دستگاه «دزدگیر» ساخته است.</p><p>او می‌گوید که این دستگاه برای محافظت از اماکن کارایی مفید دارد.</p><p>محمد مهران این ابتکار خود را به نمایش گذاشت و از طرف بازدیدکنندگان مورد تشویق قرار گرفت.</p><p>مولوی نورالله صلاح الدین، آمر تعلیمات تخنیکی و مسلکی بلخ هم این دستگاه دزدگیر را از نزدیک مشاهده نمود و سایر شاگردان را به خلاقیت و نوآوری تشویق کرد.</p><p>انستیتوت نفت و گاز بلخ دارای شاگردان و محصلین خلاق است.</p><p>حشمت‌الله یکی از فارغ التحصیلان رشته تخنیک موتر، انستیتوت نفت و گاز ولایت بلخ هم یک موتر شش تایره ساخته است.</p>', 'بلخ، مزارشریف', 'uploads/N4uHicI8wej57oUTW9HpGnyu7S67s5.jpg', 0, 'محمد مهران', 'محمد امین', '.', '2023-12-10', '1445-05-26', '1402-9-19', 'ساخت دستگاه دزدگیر', 'محمد مهران  فرزند محمد امین، یکی از شاگردان صنف دهم رشته الکترونیک انستیتوت نفت و گاز ولایت بلخ، دستگاه «دزدگیر» ساخته است.\r\n\r\nاو می‌گوید که این دستگاه برای محافظت از اماکن کارایی مفید دارد.', 1, 0, 9, 185, 3, '2023-12-10 00:28:04', '2023-12-10 00:28:04');

