--
-- Table structure for table `jobs`
--

CREATE TABLE `jobs` (
  `id` bigint(20) UNSIGNED NOT NULL,
  `title` varchar(255) NOT NULL,
  `slug` varchar(255) DEFAULT NULL,
  `content` text NOT NULL,
  `duties_and_responsibilities` text NOT NULL,
  `job_requirements` text NOT NULL,
  `submission_guideline` text NOT NULL,
  `address_dari` varchar(255) NOT NULL,
  `status` tinyint(1) NOT NULL,
  `views` int(11) NOT NULL DEFAULT 0,
  `gender` tinyint(1) NOT NULL,
  `minimum_education` varchar(255) NOT NULL,
  `salary` varchar(255) DEFAULT NULL,
  `vacancy_number` varchar(255) DEFAULT NULL,
  `no_of_jobs` int(11) DEFAULT NULL,
  `year_of_experience` varchar(255) NOT NULL,
  `email` varchar(255) NOT NULL,
  `file` varchar(255) NOT NULL,
  `start_date_gregorian` varchar(255) NOT NULL,
  `start_date_shamsi` varchar(255) NOT NULL,
  `start_date_ghamari` varchar(255) NOT NULL,
  `expire_date_gregorian` varchar(255) NOT NULL,
  `expire_date_shamsi` varchar(255) NOT NULL,
  `expire_date_ghamari` varchar(255) NOT NULL,
  `user_id` bigint(20) UNSIGNED NOT NULL,
  `job_category_id` bigint(20) UNSIGNED NOT NULL,
  `province_id` bigint(20) UNSIGNED NOT NULL,
  `district_id` bigint(20) UNSIGNED NOT NULL,
  `contract_type_id` bigint(20) UNSIGNED NOT NULL,
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

