INSERT INTO `procedures` (`id`, `language`, `subject`, `slug`, `content`, `file`, `image`, `meta_title`, `meta_description`, `status`, `created_at`, `updated_at`) VALUES
(1, 'fa', 'طرزالعمل پلانگذاری و گزارش دهی', 'طرزالعمل-پلانگذاری-و-گزارش-دهی', '<p>این طرزالعمل بخاطر انسجام و هماهنگی بهتر امور پلانگذاری و گزارشدهی دفتر مرکزی و دفاتر ولایتی،‌تهیه و ترتیب گردیده است.</p>', 'uploads/UaBEkXqCPxjwW0aF3FHPVFC2jaFoo0.jpg', 'uploads/5GU0bRo2sWAiFEAGt702YivzF81CvR.jpg', 'طرزالعمل پلانگذاری و گزارش دهی', 'این طرزالعمل بخاطر انسجام و هماهنگی بهتر امور پلانگذاری و گزارشدهی دفتر مرکزی و دفاتر ولایتی،‌تهیه و ترتیب گردیده است.', 1, '2023-12-11 05:34:47', '2023-12-11 05:34:47'),
(2, 'fa', 'طرزالعمل جوازدهی و تنظیم امور کورس های حرفوی و کوتاه مدت خصوصی', 'طرزالعمل-جوازدهی-و-تنظیم-امور-کورس-های-حرفوی-و-کوتاه-مدت-خصوصی', '<p>این طرزالعمل به منظور بدست آوردن اهداف ذیل ترتیب گردیده است:</p><ol><li>تنظیم امور مربوط به تأسیس، فعالیت و شرایط پذیرش شاگردان مراکز آموزشی حرفوی کوتاه مدت خصوصی.</li><li>افزایش دسترسی اتباع کشور به آموزش های حرفوی کوتاه مدت خصوصی.</li><li>فراهم نمودن زمینه سهم گیری و مشارکت متشبثین خصوصی در انکشاف آموزش های حرفوی و رفع نیازمندی های کشور به منابع بشری ماهر از طریق توسعه آموزش های حرفوی خصوصی.</li><li>توقویه و ارتقای سطح مهارت از طریق ایجاد و تقویه همکاری میان موسسات خصوصی و دولتی تعلیمات تخنیکی، مسلکی حرفوی.</li></ol>', 'uploads/aabU7OOVQo7MjFFBCJ2wTqhrkRvSF5.jpg', 'uploads/UpTmsl4ZdcEgiuep30eH4BZjIOQFmQ.jpg', 'طرزالعمل جوازدهی و تنظیم امور کورس های حرفوی و کوتاه مدت خصوصی', 'افزایش دسترسی اتباع کشور به آموزش های حرفوی کوتاه مدت خصوصی.', 1, '2023-12-11 05:44:59', '2023-12-11 05:44:59');

