INSERT INTO `sliders` (`id`, `image`, `sub_heading`, `description`, `created_at`, `updated_at`) VALUES
(1, 'image/images/image_1699699463.jpg', 'جلسه رهبری اداره تعلیمات تخنیکی و مسلکی', 'جلسه هیأت رهبری اداره تعلیمات تخنیکی و مسلکی به ریاست مولوی غلام حیدر شهامت، سرپرست و حضور معاونین این اداره برگزار شد.', '2023-11-11 06:14:24', '2023-11-11 06:14:24'),
(5, 'image/images/image_1699786173.jpg', 'اهدای تقدیر نامه به فارغان اداره تعلیمات تخنیکی و مسلکی', 'اهدای تقدیر نامه به فارغان اداره تعلیمات تخنیکی و مسلکی', '2023-11-12 06:19:33', '2023-11-12 06:19:33'),
(10, 'image/images/image_1699786737.jpg', 'موتر ساخته شده در اداره تعلیمات تخنیکی و مسلکی', 'موتر ساخته شده در اداره تعلیمات تخنیکی و مسلکی', '2023-11-12 06:28:57', '2023-11-12 06:28:57'),
(14, 'image/images/image_1699851855.jpg', 'امتحان الکترونیکی بست‌های استادی اداره تعلیمات تخنیکی و مسلکی', 'امتحان الکترونیکی 186بست استادی مراکز آموزشی اداره تعلیمات تخنیکی و مسلکی افتتاح شد.\r\nدر این مراسم، مولوی غلام حیدر شهامت، سرپرست اداره تعلیمات تخنیکی و مسلکی و مولوی سیف‌الدین تائب، معاون نظارت و پالیسی اداره امور نیز حضور داشتند.', '2023-11-13 00:34:16', '2023-11-13 00:34:16'),
(15, 'image/images/image_1699852268.jpg', 'گفتگو مولوی غلام حیدر شهامت با مراجعین این اداره', 'مولوی غلام حیدر شهامت، سرپرست اداره تعلیمات تخنیکی و مسلکی مشکلات مراجعین این اداره را شنید و به مکاتب اداری آنان رسیدگی کرد.', '2023-11-13 00:41:08', '2023-11-13 00:41:08'),
(16, 'image/images/image_1699852490.jpg', 'فراغت محصلین انستیتوت اداره و حسابداری کندهار', 'جشن فارغ التحصیلی 226 از نفر دانش آموختگان انستیتوت اداره و حسابداری کندهار.', '2023-11-13 00:44:50', '2023-11-13 00:44:50'),
(18, 'image/images/image_1699853746.jpg', 'مراسم افتتاحیه توزیع چهار صد هزار جلد کتاب درسی', 'اداره تعلیمات تخنیکی و مسلکی در سال گذشته موفق به چاپ 400 هزار جلد کتاب درسی در بخش‌های مسلکی از جمله زراعت، تخنیک وسایط، انجنیری ساختمانی و کمپیوتر شده بود که این کتاب‌ها به مراکز آموزشی این ادراه توزیع شد.', '2023-11-13 01:05:46', '2023-11-13 01:05:46'),
(19, 'image/images/image_1699957584.jpg', 'برگزاری نمایشگاه سه روزه تحصیلی تحت عنوان «تعلیم، مسلک و تحصیل»', 'کنفرانس و نمایشگاه تحصیلی «تعلیم، مسلک و تحصیل» با حضور مولوی غلام حیدر شهامت، سرپرست اداره تعلیمات تخنیکی و مسلکی و سایر مقامات ارشد امارت اسلامی افغانستان.', '2023-11-14 05:56:24', '2023-11-14 05:56:24'),
(20, 'image/images/image_1699957659.jpg', 'افتتاح ورکشاپ تخنیکی ترمیم موتر اداره تعلیمات تخنیکی و مسلکی', 'هدف از ایجاد این ورکشاپ فراهم ساختن زمینه آموزش عملی برای محصلین و ارتقای ظرفیت استادان است.', '2023-11-14 05:57:39', '2023-11-14 05:57:39'),
(21, 'image/images/image_1699957877.jpg', 'دور اول کورس کوتاه مدت حرفوی در اکادمی اتو میخانیکی کابل', '70 نفر از دور اول کورس کوتاه مدت حرفوی در اکادمی اتو میخانیکی کابل فارغ شدند.', '2023-11-14 06:01:17', '2023-11-14 06:01:17');

